var Validation = {
    type: "",

    // 모든 글자가 알파벳numeric 이면 true
    isAlpaNumeric: function (str) {
        var invalidCharactersRegExp = /[^a-z0-9.,;%$#@& ]/i;
        var isValid = !(invalidCharactersRegExp.test(str));
        return isValid;
    },

    //  숫자만 true
    isOnlyNumeric: function (str) {
        var invalidCharactersRegExp = /[^\d]/;
        var isValid = !(invalidCharactersRegExp.test(str));
        return isValid;
    },

    //  날짜만 true
    isOnlyDate: function (yyyy, mm, dd) {
        var isValid = true;
        var enteredDate = new Date(dd + " " + Validate.prototype.MMtoMon(mm) + " " + yyyy);
        if (enteredDate.getDate() != dd) { isValid = false; }
        return isValid;
    },

    // IS DATE ( yyyymmdd ) 닷컴 _script/util/validate.js 와 동일
    isOnlyDateYMD: function (ymd) {
        var yyyy, mm, dd
        if (ymd.length != 8) { return false; } 	// length : year(4), month(2), day(2)
        if (!this.isOnlyNumeric(ymd)) { return false; } // check number
        if (ymd.substring(0, 2) != "19" && ymd.substring(0, 2) != "20") { return false; } 		// year > 2999
        yyyy = parseInt(ymd.substring(0, 4)); 					// year string -> integer
        if (ymd.charAt(4) == "0") mm = parseInt(ymd.charAt(5));
        else mm = parseInt(ymd.substring(4, 6))					// month string -> integer
        if (ymd.charAt(6) == "0") dd = parseInt(ymd.charAt(7));
        else dd = parseInt(ymd.substring(6, 8))							// day string -> integer
        if (mm == 0 || dd == 0 || mm > 12 || dd > 31 || (mm == 2 && dd > 29) || (mm == 2 && yyyy % 4 != 0 && dd > 28)) return false;
        else if ((mm == 4 || mm == 6 || mm == 9 || mm == 11) && dd > 30) return false;
        return true;
    },

    // IS DATE ( yyyymmdd ) isOnlyDateYMD + now 이전체크
    isOnlyDateBirth: function (ymd) {
        var birth = new Date(ymd.substring(0, 4), ymd.substring(4, 6) - 1, ymd.substring(6, 8));
        var now = new Date();
        if (!this.isOnlyDateYMD(ymd)) { return false; }
        if (birth > now) { return false; }
        return true;
    },

    //  휴대폰 true
    isValidCellPhone: function (cellphone) {
        if (cellphone.substring(0, 3) != "010" && cellphone.substring(0, 3) != "011" && cellphone.substring(0, 3) != "016" && cellphone.substring(0, 3) != "017" && cellphone.substring(0, 3) != "018" && cellphone.substring(0, 3) != "019") {
            return false;
        } else {
            return true;
        }
    },

    isValidEmail: function (email) {
        var invalidCharactersRegExp = /^\w([a-zA-Z_0-9\-\.])*@\w(\.?[-\w])*\.([a-z]{3}(\.[a-z]{2})?|[a-z]{2}(\.[a-z]{2})?)$/i;
        var isValid = invalidCharactersRegExp.test(email);
        return isValid;
    },

    fc_onlyNum_check: function () {
        var key = event.keyCode;
        if (!(key == 8 || key == 9 || key == 13 || key == 46 || key == 144 || (key >= 48 && key <= 57) || key == 110 || key == 190 || (key >= 37 && key <= 40))) {
            event.returnValue = false;
        }
    }


};
